job.command:
starver &starVersion;

setenv PRODPATH &basePath;/production/&prodId;
setenv LOGPATH  &basePath;/jobs/&prodId;/log

mkdir -p ${PRODPATH} ${LOGPATH}

if ("&mMode;" == "1") then
     &basePath;/jobs/&prodId;/starSubmit/parseFileList.csh ${FILELIST} &basePath; &prodId; &mMode; &treeName; ${JOBID} &rootMacro; picoList_bad.list &productionBasePath; &starVersion;
else
    root -q -b -l StRoot/macros/loadSharedHFLibraries.C StRoot/macros/&rootMacro;++\(\"$FILELIST\",\"&prodId;_$JOBINDEX\",&mMode;,\"picoList_bad.list\",\"&treeName;\",\"&productionBasePath;\"\) >& ${JOBID}.log
    tar -zcvf ${JOBID}.log.tgz ${JOBID}.log
endif
